import React, { useEffect, useState } from 'react'
import '../styles/Bookings.css'
import axios from 'axios';
import { jsPDF } from "jspdf"; 

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const [userType, setUserType] = useState(""); 
  const [searchTerm, setSearchTerm] = useState(""); // NEW: Search state
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    fetchBookings();
    fetchUserData();
  }, [])

  const fetchUserData = async () => {
    try {
      const response = await axios.get(`http://localhost:6001/fetch-user/${userId}`);
      setUserType(response.data.usertype);
    } catch (err) {
      console.log("Error fetching user role", err);
    }
  }

  const fetchBookings = async () => {
    await axios.get('http://localhost:6001/fetch-bookings').then(
      (response) => {
        setBookings(response.data.reverse());
      }
    )
  }

  const downloadTicket = (booking) => {
    const doc = new jsPDF();
    doc.setFontSize(22);
    doc.text("SB FLIGHTS BOARDING PASS", 40, 25);
    doc.setLineWidth(0.5);
    doc.rect(15, 35, 180, 80); 
    doc.setFontSize(12);
    doc.text(`Passenger: ${booking.email}`, 20, 50);
    doc.text(`Flight: ${booking.flightName} (${booking.flightId})`, 20, 60);
    doc.text(`From: ${booking.departure}  To: ${booking.destination}`, 20, 70);
    doc.text(`Journey Date: ${booking.journeyDate.slice(0, 10)}`, 20, 80);
    doc.setFontSize(16);
    doc.setTextColor(255, 0, 0); 
    doc.text(`SEAT(S): ${booking.seats}`, 20, 95);
    doc.save(`Ticket_${booking._id}.pdf`);
  };

  const cancelTicket = async (id) => {
    await axios.put(`http://localhost:6001/cancel-ticket/${id}`).then(
      (response) => {
        alert("Ticket cancelled!!");
        fetchBookings();
      }
    )
  }

  return (
    <div className="user-bookingsPage">
      <div className="bookings-header" style={{textAlign: 'center', marginBottom: '30px'}}>
        <h1>{userType === 'flight-operator' || userType === 'admin' ? "All Bookings" : "My Bookings"}</h1>
        
        {/* NEW: Search Input UI */}
        {(userType === 'flight-operator' || userType === 'admin') && (
            <div className="search-container" style={{marginTop: '20px'}}>
                <input 
                    type="text" 
                    placeholder="Search by Email, Flight ID, or Passenger Name..." 
                    className="form-control w-50 m-auto"
                    onChange={(e) => setSearchTerm(e.target.value.toLowerCase())}
                    style={{padding: '10px', borderRadius: '5px', border: '1px solid #ccc'}}
                />
            </div>
        )}
      </div>
      
      <div className="user-bookings">
        {bookings
          .filter(booking => {
            // First Level: Role Based Filtering
            let roleMatch = false;
            if (userType === 'flight-operator' || userType === 'admin') {
              roleMatch = true;
            } else {
              roleMatch = booking.user === userId;
            }

            // Second Level: Search Term Filtering (only if roleMatch is true)
            if (!roleMatch) return false;
            if (searchTerm === "") return true;

            const nameMatch = booking.passengers.some(p => p.name.toLowerCase().includes(searchTerm));
            const emailMatch = booking.email.toLowerCase().includes(searchTerm);
            const flightMatch = booking.flightId.toLowerCase().includes(searchTerm);

            return nameMatch || emailMatch || flightMatch;
          })
          .map((booking) => {
            return (
              <div className="user-booking" key={booking._id}>
                <p><b>Booking ID:</b> {booking._id}</p>
                
                {(userType === 'flight-operator' || userType === 'admin') && (
                  <p style={{color: 'blue'}}><b>Customer UserID:</b> {booking.user}</p>
                )}
                
                <span>
                  <p><b>Mobile:</b> {booking.mobile}</p>
                  <p><b>Email:</b> {booking.email}</p>
                </span>
                <span>
                  <p><b>Flight Id:</b> {booking.flightId}</p>
                  <p><b>Flight name:</b> {booking.flightName}</p>
                </span>
                <span>
                  <p><b>On-boarding:</b> {booking.departure}</p>
                  <p><b>Destination:</b> {booking.destination}</p>
                </span>
                <span>
                  <div>
                    <p><b>Passengers:</b></p>
                    <ol>
                      {booking.passengers.map((passenger, i) => (
                        <li key={i}><p><b>Name:</b> {passenger.name},  <b>Age:</b> {passenger.age}</p></li>
                      ))}
                    </ol>
                  </div>
                  {booking.bookingStatus === 'confirmed' ? <p><b>Seats:</b> {booking.seats}</p> : ""}
                </span>
                <span>
                  <p><b>Booking date:</b> {booking.bookingDate.slice(0, 10)}</p>
                  <p><b>Journey date:</b> {booking.journeyDate.slice(0, 10)}</p>
                </span>
                <span>
                  <p><b>Journey Time:</b> {booking.journeyTime}</p>
                  <p><b>Total price:</b> {booking.totalPrice}</p>
                </span>
                <p style={{color: booking.bookingStatus === 'cancelled' ? 'red' : 'black'}}>
                  <b>Booking status:</b> {booking.bookingStatus}
                </p>

                {booking.bookingStatus === 'confirmed' && (
                  <div style={{display: 'flex', gap: '15px'}}>
                    <button className="btn btn-danger" onClick={() => cancelTicket(booking._id)}>Cancel Ticket</button>
                    <button className="btn btn-success" onClick={() => downloadTicket(booking)}>Download Ticket (PDF)</button>
                  </div>
                )}
              </div>
            )
          })}
      </div>
    </div>
  )
}

export default Bookings
