import React, { useEffect, useState, useCallback } from 'react';
import '../styles/NewFlight.css';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

const EditFlight = () => {
    const { id } = useParams();
    const navigate = useNavigate();

    // 1. Initialize all state with empty strings/zeros to prevent "uncontrolled component" warnings
    const [flightName, setFlightName] = useState('');
    const [flightId, setFlightId] = useState('');
    const [origin, setOrigin] = useState('');
    const [destination, setDestination] = useState('');
    const [startTime, setStartTime] = useState('');
    const [arrivalTime, setArrivalTime] = useState('');
    const [totalSeats, setTotalSeats] = useState(0);
    const [basePrice, setBasePrice] = useState(0);

    // 2. Define fetch function BEFORE useEffect
    const fetchFlightData = useCallback(async () => {
        try {
            const response = await axios.get(`http://localhost:6001/fetch-flight/${id}`);
            const data = response.data;

            setFlightName(data.flightName || '');
            setFlightId(data.flightId || '');
            setOrigin(data.origin || '');
            setDestination(data.destination || '');
            setTotalSeats(data.totalSeats || 0);
            setBasePrice(data.basePrice || 0);

            // Helper to format time strings for <input type="time" />
            const formatTime = (timeStr) => {
                if (!timeStr) return '';
                const parts = timeStr.split(":");
                return `${parts[0].padStart(2, '0')}:${parts[1].padStart(2, '0')}`;
            };

            setStartTime(formatTime(data.departureTime));
            setArrivalTime(formatTime(data.arrivalTime));
        } catch (error) {
            console.error("Error fetching flight:", error);
        }
    }, [id]);

    useEffect(() => {
        fetchFlightData();
    }, [fetchFlightData]);

    const handleSubmit = async () => {
        const inputs = {
            _id: id,
            flightName,
            flightId,
            origin,
            destination,
            departureTime: startTime,
            arrivalTime,
            basePrice,
            totalSeats
        };

        try {
            await axios.put('http://localhost:6001/update-flight', inputs);
            alert('Flight updated successfully!!');
            navigate('/admin-flights'); // Redirect back to the admin flight list
        } catch (err) {
            console.error("Update failed:", err);
            alert('Update failed. Please check the console.');
        }
    };

    return (
        <div className='NewFlightPage'>
            <div className="NewFlightPageContainer">
                <h2>Edit Flight</h2>

                <span className='newFlightSpan1'>
                    <div className="form-floating mb-3">
                        {/* REMOVED 'disabled' so you can actually edit the name if needed */}
                        <input 
                            type="text" 
                            className="form-control" 
                            id="flightNameInput" 
                            value={flightName} 
                            onChange={(e) => setFlightName(e.target.value)} 
                        />
                        <label htmlFor="flightNameInput">Flight Name</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input 
                            type="text" 
                            className="form-control" 
                            id="flightIdInput" 
                            value={flightId} 
                            onChange={(e) => setFlightId(e.target.value)} 
                        />
                        <label htmlFor="flightIdInput">Flight Id</label>
                    </div>
                </span>

                <span>
                    <div className="form-floating">
                        <select 
                            className="form-select mb-3" 
                            value={origin} 
                            onChange={(e) => setOrigin(e.target.value)}
                        >
                            <option value="" disabled>Select Departure City</option>
                            <option value="Chennai">Chennai</option>
                            <option value="Banglore">Banglore</option>
                            <option value="Hyderabad">Hyderabad</option>
                            <option value="Mumbai">Mumbai</option>
                            <option value="Indore">Indore</option>
                            <option value="Delhi">Delhi</option>
                            <option value="Pune">Pune</option>
                            <option value="Trivendrum">Trivendrum</option>
                            <option value="Bhopal">Bhopal</option>
                            <option value="Kolkata">Kolkata</option>
                            <option value="varanasi">Varanasi</option>
                            <option value="Jaipur">Jaipur</option>
                        </select>
                        <label>Departure City</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input 
                            type="time" 
                            className="form-control" 
                            value={startTime} 
                            onChange={(e) => setStartTime(e.target.value)} 
                        />
                        <label>Departure Time</label>
                    </div>
                </span>

                <span>
                    <div className="form-floating">
                        <select 
                            className="form-select mb-3" 
                            value={destination} 
                            onChange={(e) => setDestination(e.target.value)}
                        >
                            <option value="" disabled>Select Destination City</option>
                            <option value="Chennai">Chennai</option>
                            <option value="Banglore">Banglore</option>
                            <option value="Hyderabad">Hyderabad</option>
                            <option value="Mumbai">Mumbai</option>
                            <option value="Indore">Indore</option>
                            <option value="Delhi">Delhi</option>
                            <option value="Pune">Pune</option>
                            <option value="Trivendrum">Trivendrum</option>
                            <option value="Bhopal">Bhopal</option>
                            <option value="Kolkata">Kolkata</option>
                            <option value="varanasi">Varanasi</option>
                            <option value="Jaipur">Jaipur</option>
                        </select>
                        <label>Destination City</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input 
                            type="time" 
                            className="form-control" 
                            value={arrivalTime} 
                            onChange={(e) => setArrivalTime(e.target.value)} 
                        />
                        <label>Arrival time</label>
                    </div>
                </span>

                <span className='newFlightSpan2'>
                    <div className="form-floating mb-3">
                        <input 
                            type="number" 
                            className="form-control" 
                            value={totalSeats} 
                            onChange={(e) => setTotalSeats(e.target.value)} 
                        />
                        <label>Total seats</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input 
                            type="number" 
                            className="form-control" 
                            value={basePrice} 
                            onChange={(e) => setBasePrice(e.target.value)} 
                        />
                        <label>Base price</label>
                    </div>
                </span>

                <button className='btn btn-primary w-100' onClick={handleSubmit}>Update Flight</button>
            </div>
        </div>
    );
};

export default EditFlight;
