import React, { useEffect, useState } from 'react'
import '../styles/Bookings.css'
import axios from 'axios';
import { jsPDF } from "jspdf"; 
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const FlightBookings = () => {
  const [bookings, setBookings] = useState([]);
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    fetchBookings();
  }, [])

  const fetchBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-bookings');
      setBookings(response.data.reverse());
    } catch (err) {
      console.error("Error fetching bookings", err);
    }
  }

  // DATA PROCESSING FOR CHART
  const prepareChartData = () => {
    const flightMap = {};

    bookings.forEach(booking => {
      // We only chart confirmed bookings
      if (booking.bookingStatus === 'confirmed') {
        const name = booking.flightName;
        if (!flightMap[name]) {
          flightMap[name] = {
            name: name,
            filled: 0,
            capacity: booking.totalCapacity || 60
          };
        }
        flightMap[name].filled += booking.passengers.length;
      }
    });

    return Object.values(flightMap);
  };

  const downloadTicket = (booking) => {
    const doc = new jsPDF();
    doc.text("FLIGHT TICKET", 80, 20);
    doc.text(`Booking ID: ${booking._id}`, 20, 40);
    doc.text(`Passenger: ${booking.email}`, 20, 50);
    doc.save(`Ticket_${booking._id}.pdf`);
  };

  return (
    <div className="user-bookingsPage">
      <h1 style={{textAlign: 'center', margin: '20px 0'}}>Operator Dashboard</h1>

      {/* OCCUPANCY CHART SECTION */}
      <div style={{ 
          width: '90%', 
          height: '350px', 
          margin: '0 auto 40px auto', 
          background: 'white', 
          padding: '25px', 
          borderRadius: '15px', 
          boxShadow: '0 4px 20px rgba(0,0,0,0.08)' 
      }}>
        <h3 style={{ textAlign: 'center', marginBottom: '20px', color: '#2c3e50' }}>Live Flight Occupancy</h3>
        <ResponsiveContainer width="100%" height="90%">
          <BarChart data={prepareChartData()}>
            <CartesianGrid strokeDasharray="3 3" vertical={false} />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend iconType="circle" />
            <Bar dataKey="filled" name="Booked Seats" fill="#3498db" radius={[4, 4, 0, 0]} />
            <Bar dataKey="capacity" name="Total Capacity" fill="#ecf0f1" radius={[4, 4, 0, 0]} />
          </BarChart>
        </ResponsiveContainer>
      </div>

      <div className="user-bookings">
        {bookings.map((booking) => (
          <div className="user-booking" key={booking._id}>
            <p><b>Booking ID:</b> {booking._id}</p>
            <span>
                <p><b>Email:</b> {booking.email}</p>
                <p><b>Flight:</b> {booking.flightName} ({booking.flightId})</p>
            </span>
            <span>
                <p><b>Route:</b> {booking.departure} â†’ {booking.destination}</p>
                <p><b>Journey Date:</b> {booking.journeyDate.slice(0, 10)}</p>
            </span>
            <div>
                <p><b>Passengers & Seats:</b></p>
                <ul>
                    {booking.passengers.map((p, i) => (
                        <li key={i}>{p.name} (Age: {p.age})</li>
                    ))}
                </ul>
                <p style={{color: 'green', fontWeight: 'bold'}}>Seats: {booking.seats}</p>
            </div>
            <p><b>Status:</b> 
                <span style={{color: booking.bookingStatus === 'cancelled' ? 'red' : 'green'}}>
                     {booking.bookingStatus}
                </span>
            </p>
            {booking.bookingStatus === 'confirmed' && (
                <button className="btn btn-success btn-sm mt-2" onClick={() => downloadTicket(booking)}>
                    Download PDF
                </button>
            )}
          </div>
        ))}
      </div>
    </div>
  )
}

export default FlightBookings;
