import React, { useEffect, useState } from 'react'
import '../styles/Bookings.css'
import axios from 'axios';
import { jsPDF } from "jspdf"; 

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const [userRole, setUserRole] = useState(""); 
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    const fetchData = async () => {
      // 1. Fetch User details to verify role
      try {
        const userRes = await axios.get(`http://localhost:6001/fetch-user/${userId}`);
        setUserRole(userRes.data.usertype);
        
        // 2. Fetch All Bookings
        const bookingsRes = await axios.get('http://localhost:6001/fetch-bookings');
        setBookings(bookingsRes.data.reverse());
      } catch (err) {
        console.error("Initialization error:", err);
      }
    };
    fetchData();
  }, [userId]);

  const downloadTicket = (booking) => {
    const doc = new jsPDF();
    doc.text("FLIGHT TICKET", 80, 20);
    doc.text(`Booking ID: ${booking._id}`, 20, 40);
    doc.text(`Flight: ${booking.flightName}`, 20, 50);
    doc.text(`Seats: ${booking.seats}`, 20, 60);
    doc.save(`Ticket_${booking._id}.pdf`);
  };

  return (
    <div className="user-bookingsPage">
      {/* Visual confirmation of the detected role */}
      <h1 style={{textAlign:'center', color: '#366878'}}>
        {userRole === 'flight-operator' || userRole === 'admin' ? "Global Bookings Management" : "My Bookings"}
      </h1>

      <div className="user-bookings">
        {bookings
          .filter(booking => {
            // THE CRITICAL FIX:
            // If the user is an admin or operator, return TRUE for every booking (Show All)
            if (userRole === 'admin' || userRole === 'flight-operator') {
              return true;
            }
            // Otherwise, only show if the booking belongs to this user
            return booking.user === userId;
          })
          .map((booking) => (
            <div className="user-booking" key={booking._id}>
              <div className="booking-card-content">
                <p><b>Booking ID:</b> {booking._id}</p>
                
                {/* For Operator: Show who the customer is */}
                {(userRole === 'admin' || userRole === 'flight-operator') && (
                  <p className="badge bg-primary">Customer ID: {booking.user}</p>
                )}

                <div className="info-grid">
                  <p><b>Flight:</b> {booking.flightName} ({booking.flightId})</p>
                  <p><b>Route:</b> {booking.departure} to {booking.destination}</p>
                  <p><b>Email:</b> {booking.email}</p>
                  <p><b>Seats:</b> <span style={{color: 'green'}}>{booking.seats || "Not Assigned"}</span></p>
                </div>

                <div className="passenger-list">
                  <b>Passengers:</b>
                  <ul>
                    {booking.passengers.map((p, i) => (
                      <li key={i}>{p.name} (Age: {p.age})</li>
                    ))}
                  </ul>
                </div>

                <p><b>Status:</b> 
                  <span style={{color: booking.bookingStatus === 'cancelled' ? 'red' : 'green', marginLeft: '5px'}}>
                    {booking.bookingStatus}
                  </span>
                </p>

                {booking.bookingStatus === 'confirmed' && (
                  <button className="btn btn-success mt-2" onClick={() => downloadTicket(booking)}>
                    Download PDF
                  </button>
                )}
              </div>
            </div>
          ))}
      </div>
    </div>
  )
}

export default Bookings;
