import React, { useState, useEffect } from 'react';
import '../styles/NewFlight.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const NewFlight = () => {
    const navigate = useNavigate();

    // 1. Initialize states as empty
    const [flightName, setFlightName] = useState('');
    const [flightId, setFlightId] = useState('');
    const [origin, setOrigin] = useState('');
    const [destination, setDestination] = useState('');
    const [startTime, setStartTime] = useState('');
    const [arrivalTime, setArrivalTime] = useState('');
    const [totalSeats, setTotalSeats] = useState(0);
    const [basePrice, setBasePrice] = useState(0);

    // 2. Force clear fields when the component mounts (prevents "Hafeeza" from staying)
    useEffect(() => {
        setFlightName('');
        setFlightId('');
    }, []);

    const handleAddFlight = async () => {
        const inputs = { flightName, flightId, origin, destination, departureTime: startTime, arrivalTime, basePrice, totalSeats };
        try {
            await axios.post('http://localhost:6001/add-flight', inputs);
            alert('New Flight Added!!');
            navigate('/flights');
        } catch (err) {
            alert('Failed to add flight');
        }
    };

    return (
        <div className='NewFlightPage'>
            <div className="NewFlightPageContainer">
                <h2>Add New Flight</h2>
                <span className='newFlightSpan1'>
                    <div className="form-floating mb-3">
                        {/* REMOVED 'disabled' HERE SO YOU CAN TYPE */}
                        <input type="text" className="form-control" value={flightName} onChange={(e) => setFlightName(e.target.value)} />
                        <label>Flight Name</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="text" className="form-control" value={flightId} onChange={(e) => setFlightId(e.target.value)} />
                        <label>Flight Id</label>
                    </div>
                </span>
                {/* ... other input fields remain the same ... */}
                <button className='btn btn-primary' onClick={handleAddFlight}>Add now</button>
            </div>
        </div>
    );
};

export default NewFlight;
