import axios from 'axios';
import React, { useEffect, useState } from 'react'

const FlightBookings = () => {
  const [userDetails, setUserDetails] = useState();
  const [bookings, setBookings] = useState([]);

  useEffect(()=>{
    fetchUserData(); 
    fetchBookings();
  }, [])

  const fetchUserData = async () =>{
    try{
      const id = localStorage.getItem('userId');

      const response = await axios.get(
        `http://localhost:6001/fetch-user/${id}`
      );

      setUserDetails(response.data);

    }catch(err){
      console.log(err);
    }
  } 

  const fetchBookings = async () =>{
    try{
      const flightName = localStorage.getItem('username');

      const response = await axios.get(
        `http://localhost:6001/fetch-operator-bookings/${flightName}`
      );

      setBookings(response.data.reverse());

    }catch(err){
      console.log(err);
    }
  }

  const cancelTicket = async (id) =>{
    try{
      await axios.put(`http://localhost:6001/cancel-ticket/${id}`);
      alert("Ticket cancelled!!");
      fetchBookings();   // refresh after cancel
    }catch(err){
      console.log(err);
    }
  }

  return (
    <div className="user-bookingsPage">

      {userDetails ?
        <>
          {userDetails.approval === 'not-approved' ?
            <div className="notApproved-box">
              <h3>Approval Required!!</h3>
              <p>
                Your application is under processing. 
                It needs an approval from the administrator.
              </p>
            </div>

          : userDetails.approval === 'approved' ?
            <>
              <h1>Bookings</h1>

              <div className="user-bookings">

                {bookings.map((booking)=>(
                  
                  <div className="user-booking" key={booking._id}>

                    <p><b>Booking ID:</b> {booking._id}</p>

                    <span>
                      <p><b>Mobile:</b> {booking.mobile}</p>
                      <p><b>Email:</b> {booking.email}</p>
                    </span>

                    <span>
                      <p><b>Flight Id:</b> {booking.flightId}</p>
                      <p><b>Flight name:</b> {booking.flightName}</p>
                    </span>

                    <span>
                      <p><b>On-boarding:</b> {booking.departure}</p>
                      <p><b>Destination:</b> {booking.destination}</p>
                    </span>

                    <span>
                      <div>
                        <p><b>Passengers:</b></p>
                        <ol>
                          {booking.passengers.map((passenger, i)=>(
                            <li key={i}>
                              <p>
                                <b>Name:</b> {passenger.name},  
                                <b> Age:</b> {passenger.age}
                              </p>
                            </li>
                          ))}
                        </ol>
                      </div>

                      {booking.bookingStatus === 'confirmed' && 
                        <p><b>Seats:</b> {booking.seats}</p>
                      }
                    </span>

                    <span>
                      <p><b>Booking date:</b> {booking.bookingDate?.slice(0,10)}</p>
                      <p><b>Journey date:</b> {booking.journeyDate?.slice(0,10)}</p>
                    </span>

                    <span>
                      <p><b>Journey Time:</b> {booking.journeyTime}</p>
                      <p><b>Total price:</b> {booking.totalPrice}</p>
                    </span>

                    {booking.bookingStatus === 'cancelled' ?
                      <p style={{color: "red"}}>
                        <b>Booking status:</b> {booking.bookingStatus}
                      </p>
                      :
                      <p>
                        <b>Booking status:</b> {booking.bookingStatus}
                      </p>
                    }

                    {booking.bookingStatus === 'confirmed' &&
                      <div>
                        <button 
                          className="btn btn-danger"
                          onClick={()=> cancelTicket(booking._id)}
                        >
                          Cancel Ticket
                        </button>
                      </div>
                    }

                  </div>
                ))}

              </div>
            </>
          :
            ""
          }
        </>
      :
       ""
      }

    </div>
  )

}

export default FlightBookings
