import React, { useState, useEffect } from 'react';
import '../styles/NewFlight.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const NewFlight = () => {
    const navigate = useNavigate();

    // Always start with empty values for a NEW flight
    const [flightName, setFlightName] = useState('');
    const [flightId, setFlightId] = useState('');
    const [origin, setOrigin] = useState('');
    const [destination, setDestination] = useState('');
    const [startTime, setStartTime] = useState('');
    const [arrivalTime, setArrivalTime] = useState('');
    const [totalSeats, setTotalSeats] = useState(0);
    const [basePrice, setBasePrice] = useState(0);

    const handleAddFlight = async () => {
        const newFlight = {
            flightName,
            flightId,
            origin,
            destination,
            departureTime: startTime,
            arrivalTime,
            basePrice,
            totalSeats
        };

        try {
            await axios.post('http://localhost:6001/add-flight', newFlight);
            alert('New Flight Added Successfully!');
            navigate('/flights'); // Go to the list to see the new entry
        } catch (err) {
            console.error(err);
            alert('Failed to add flight.');
        }
    };

    return (
        <div className='NewFlightPage'>
            <div className="NewFlightPageContainer">
                <h2>Add New Flight</h2>
                <span className='newFlightSpan1'>
                    <div className="form-floating mb-3">
                        <input type="text" className="form-control" value={flightName} onChange={(e) => setFlightName(e.target.value)} />
                        <label>Flight Name</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="text" className="form-control" value={flightId} onChange={(e) => setFlightId(e.target.value)} />
                        <label>Flight Id</label>
                    </div>
                </span>
                {/* ... rest of your inputs (Origin, Destination, etc.) ... */}
                <button className='btn btn-primary w-100' onClick={handleAddFlight}>Add Now</button>
            </div>
        </div>
    );
};

export default NewFlight;
