import React, { useEffect, useState } from 'react'
import '../styles/Bookings.css'
import axios from 'axios';
import { jsPDF } from "jspdf"; 

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const [userType, setUserType] = useState(""); 
  const [searchTerm, setSearchTerm] = useState(""); 
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    fetchUserData();
    fetchBookings();
  }, [])

  const fetchUserData = async () => {
    try {
      const response = await axios.get(`http://localhost:6001/fetch-user/${userId}`);
      // Ensure this matches exactly what is in your MongoDB (e.g., 'flight-operator')
      setUserType(response.data.usertype);
    } catch (err) {
      console.log("Error fetching user data", err);
    }
  }

  const fetchBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-bookings');
      // reverse() ensures the newest bookings (like the ones in your screenshot) appear at the top
      setBookings(response.data.reverse());
    } catch (err) {
      console.log("Error fetching bookings", err);
    }
  }

  const downloadTicket = (booking) => {
    const doc = new jsPDF();
    doc.setFontSize(20);
    doc.text("BOARDING PASS", 80, 20);
    doc.setFontSize(12);
    doc.text(`Flight: ${booking.flightName} (${booking.flightId})`, 20, 40);
    doc.text(`Seats: ${booking.seats}`, 20, 50);
    doc.text(`Route: ${booking.departure} to ${booking.destination}`, 20, 60);
    doc.save(`Ticket_${booking._id}.pdf`);
  };

  const cancelTicket = async (id) => {
    if(window.confirm("Confirm cancellation?")) {
        await axios.put(`http://localhost:6001/cancel-ticket/${id}`).then(() => {
              alert("Ticket cancelled successfully");
              fetchBookings();
        });
    }
  }

  return (
    <div className="user-bookingsPage">
      <div className="bookings-header" style={{textAlign: 'center', marginBottom: '30px'}}>
        {/* Dynamic Title */}
        <h1>{userType === 'flight-operator' || userType === 'admin' ? "Operator: All Bookings" : "My Bookings"}</h1>
        
        {/* Search Bar: Essential for Operator to find the new bookings */}
        {(userType === 'flight-operator' || userType === 'admin') && (
            <input 
                type="text" 
                placeholder="Search by Email, Flight, or Passenger..." 
                className="form-control w-50 m-auto mt-3"
                onChange={(e) => setSearchTerm(e.target.value.toLowerCase())}
            />
        )}
      </div>
      
      <div className="user-bookings">
        {bookings
          .filter(booking => {
            // THE FIX: If operator, ignore the userId check.
            const isStaff = (userType === 'flight-operator' || userType === 'admin');
            
            if (!isStaff && booking.user !== userId) {
                return false; 
            }

            // Search Filter
            if (searchTerm === "") return true;
            return (
                booking.email.toLowerCase().includes(searchTerm) ||
                booking.flightId.toLowerCase().includes(searchTerm) ||
                booking.passengers.some(p => p.name.toLowerCase().includes(searchTerm))
            );
          })
          .map((booking) => (
            <div className="user-booking" key={booking._id}>
                <p><b>Booking ID:</b> {booking._id}</p>
                
                {/* Show Customer ID only to Operator */}
                {(userType === 'flight-operator' || userType === 'admin') && (
                    <p style={{color: '#007bff', fontSize: '0.8rem'}}>Customer ID: {booking.user}</p>
                )}

                <div className="booking-details-grid">
                  <span>
                    <p><b>Email:</b> {booking.email}</p>
                    <p><b>Flight:</b> {booking.flightName} ({booking.flightId})</p>
                  </span>
                  <span>
                    <p><b>Route:</b> {booking.departure} â†’ {booking.destination}</p>
                    <p><b>Journey Date:</b> {booking.journeyDate.slice(0,10)}</p>
                  </span>
                  <span>
                    <div>
                      <p><b>Passengers & Seats:</b></p>
                      <ul style={{fontSize: '0.9rem'}}>
                        {booking.passengers.map((p, i) => <li key={i}>{p.name} (Age: {p.age})</li>)}
                      </ul>
                      <p style={{color: 'green', fontWeight: 'bold'}}>Seats: {booking.seats}</p>
                    </div>
                  </span>
                </div>

                <p><b>Status:</b> <span style={{color: booking.bookingStatus === 'cancelled' ? 'red' : 'green'}}>{booking.bookingStatus}</span></p>

                {booking.bookingStatus === 'confirmed' && (
                  <div className="mt-2">
                    <button className="btn btn-danger btn-sm me-2" onClick={() => cancelTicket(booking._id)}>Cancel</button>
                    <button className="btn btn-success btn-sm" onClick={() => downloadTicket(booking)}>PDF</button>
                  </div>
                )}
            </div>
          ))}
      </div>
    </div>
  )
}

export default Bookings;
