import React, { useEffect, useState } from 'react'
import '../styles/Bookings.css'
import axios from 'axios';
import { jsPDF } from "jspdf"; 

const Bookings = () => {
  const [bookings, setBookings] = useState([]);
  const [userType, setUserType] = useState(""); 
  const [searchTerm, setSearchTerm] = useState(""); 
  const userId = localStorage.getItem('userId');

  useEffect(() => {
    fetchUserData();
    fetchBookings();
  }, [])

  const fetchUserData = async () => {
    try {
      const response = await axios.get(`http://localhost:6001/fetch-user/${userId}`);
      console.log("Current User Role:", response.data.usertype); // DEBUG: Check your console
      setUserType(response.data.usertype);
    } catch (err) {
      console.log("Error fetching user role", err);
    }
  }

  const fetchBookings = async () => {
    try {
      const response = await axios.get('http://localhost:6001/fetch-bookings');
      setBookings(response.data.reverse());
    } catch (err) {
      console.log("Error fetching bookings", err);
    }
  }

  const downloadTicket = (booking) => {
    const doc = new jsPDF();
    doc.setFontSize(20);
    doc.text("FLIGHT TICKET", 80, 20);
    doc.setFontSize(12);
    doc.text(`Booking ID: ${booking._id}`, 20, 40);
    doc.text(`Flight: ${booking.flightName}`, 20, 50);
    doc.text(`Seats: ${booking.seats}`, 20, 60);
    doc.save(`Ticket_${booking._id}.pdf`);
  };

  const cancelTicket = async (id) => {
    if(window.confirm("Are you sure you want to cancel?")) {
        await axios.put(`http://localhost:6001/cancel-ticket/${id}`).then(
            (response) => {
              alert("Ticket cancelled!!");
              fetchBookings();
            }
        )
    }
  }

  return (
    <div className="user-bookingsPage">
      <div className="bookings-header" style={{textAlign: 'center', marginBottom: '30px'}}>
        <h1>{userType === 'admin' || userType === 'flight-operator' ? "Global Booking Management" : "My Bookings"}</h1>
        
        {/* Search Bar only for Management */}
        {(userType === 'admin' || userType === 'flight-operator') && (
            <input 
                type="text" 
                placeholder="Search by Passenger, Email or Flight ID..." 
                className="form-control w-50 m-auto mt-3"
                onChange={(e) => setSearchTerm(e.target.value.toLowerCase())}
            />
        )}
      </div>
      
      <div className="user-bookings">
        {bookings
          .filter(booking => {
            // 1. Role Logic: Admins and Operators see everything
            // Note: Ensure 'flight-operator' matches exactly what is in your DB
            const isManagement = (userType === 'admin' || userType === 'flight-operator');
            
            if (!isManagement && booking.user !== userId) {
                return false; // Hide if regular user and not their booking
            }

            // 2. Search Logic
            if (searchTerm === "") return true;
            const searchStr = searchTerm.toLowerCase();
            return (
                booking.email.toLowerCase().includes(searchStr) ||
                booking.flightId.toLowerCase().includes(searchStr) ||
                booking.passengers.some(p => p.name.toLowerCase().includes(searchStr))
            );
          })
          .map((booking) => (
            <div className="user-booking" key={booking._id}>
                <p><b>Booking ID:</b> {booking._id}</p>
                
                {/* Visual tag so operator knows this is a global view */}
                {(userType === 'admin' || userType === 'flight-operator') && (
                    <span className="badge bg-info text-dark mb-2">Customer ID: {booking.user}</span>
                )}

                <span>
                  <p><b>Mobile:</b> {booking.mobile}</p>
                  <p><b>Email:</b> {booking.email}</p>
                </span>
                <span>
                  <p><b>Flight:</b> {booking.flightName} ({booking.flightId})</p>
                  <p><b>Route:</b> {booking.departure} to {booking.destination}</p>
                </span>
                <span>
                  <div>
                    <p><b>Passengers:</b></p>
                    <ol>
                      {booking.passengers.map((p, i) => (
                        <li key={i}>{p.name} ({p.age})</li>
                      ))}
                    </ol>
                  </div>
                  <p><b>Seats:</b> <span style={{color: 'green', fontWeight: 'bold'}}>{booking.seats}</span></p>
                </span>
                <span>
                  <p><b>Journey Date:</b> {booking.journeyDate}</p>
                  <p><b>Total Price:</b> â‚¹{booking.totalPrice}</p>
                </span>
                <p><b>Status:</b> <span style={{color: booking.bookingStatus === 'cancelled' ? 'red' : 'green'}}>{booking.bookingStatus}</span></p>

                {booking.bookingStatus === 'confirmed' && (
                  <div className="mt-3">
                    <button className="btn btn-danger me-2" onClick={() => cancelTicket(booking._id)}>Cancel</button>
                    <button className="btn btn-success" onClick={() => downloadTicket(booking)}>Download PDF</button>
                  </div>
                )}
            </div>
          ))}
      </div>
    </div>
  )
}

export default Bookings
